<template>
  <div id="cart-main">
    <div class="title-container">購物籃</div>
    <div class="items-container">
      <Item
        v-for="(item, index) in items"
        :key="index"
        :index="index"
        v-bind="item"
        :onIncrease="increaseAmount"
        :onDecrease="decreaseAmount"
      />
    </div>
    <div class="footer-container">
      <PriceRow title="運費" :price="shippingFeeText" />
      <PriceRow title="小計" :price="priceText" />
    </div>
  </div>
</template>

<script>
import ItemVue from './Item.vue';
import PriceRowVue from './PriceRow.vue';
import getPriceText from '../../utils/getPriceText';

const items = [
  {
    imgSrc:
      'data:image/jpeg;base64,',
    title: '破壞補丁修身牛仔褲',
    amount: 1,
    price: 3999,
  },
  {
    imgSrc:
      'data:image/jpeg;base64,',
    title: '刷色直筒牛仔褲',
    amount: 1,
    price: 1299,
  },
];

export default {
  components: {
    PriceRow: PriceRowVue,
    Item: ItemVue,
  },
  props: {
    shippingFee: {
      type: Number,
      required: true,
    },
    onChange: {
      type: Function,
    },
  },
  data: function () {
    return {
      items,
    };
  },
  methods: {
    increaseAmount(index) {
      return () => {
        this.items[index].amount++;
      };
    },
    decreaseAmount(index) {
      return () => {
        if (this.items[index].amount > 1) this.items[index].amount--;
      };
    },
  },
  computed: {
    shippingFeeText() {
      return getPriceText(this.shippingFee);
    },
    priceText() {
      const price = this.items.reduce((acc, cur) => {
        return (acc += cur.amount * cur.price);
      }, 0);

      const total = price + this.shippingFee;
      this.$emit('change', total);
      return getPriceText(total);
    },
  },
};
</script>

